package basic_java;

public class swaptwonumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		System.out.println("before swapping the value of a and b is : " + a + "\t" + b);
		int temp = a;
		a = b;
		b = temp;
		System.out.println("after swapping the value of a and b is : " + a + "\t" + b);

	}

}
